import 'package:flutter/material.dart';class Counter_Screen extends StatefulWidget {  @override  State<Counter_Screen> createState() => _Counter_ScreenState();}class _Counter_ScreenState extends State<Counter_Screen> {  int Counter = 1;  @override  void initState() {    super.initState();  }  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        titleSpacing: 25.0,        title: Text(          'Counter',          style: TextStyle(            fontSize: 25.0,          ),        ),      ),      body: Center(        child: SingleChildScrollView(          scrollDirection: Axis.horizontal,            child: Row(          mainAxisAlignment: MainAxisAlignment.center,          children: [            TextButton(              onPressed: () {                setState(() {                  Counter--;                  print(Counter);                });              },              child: Text(                'Minus -',                style: TextStyle(                  fontSize: 25.0,                ),              ),            ),            Padding(              padding: const EdgeInsets.symmetric(                horizontal:25.0,              ),              child: Text(                '$Counter',                style: TextStyle(                  fontSize: 60.0,                  fontWeight: FontWeight.bold,                  color: Colors.black,                ),              ),            ),            TextButton(              onPressed: () {                setState(() {                  Counter++;                  print(Counter);                });              },              child: Text(                'Plus +',                style: TextStyle(                  fontSize: 25.0,                ),              ),            ),          ],        )),      ),    );  }}